eSoafApp.filter("ePercent",function(){return function(n){if(!n)return"0%";if(null==n)return"0%";if("number"!=typeof n)return"0%";n=100*n,n=n.toString().trim();var r=n.indexOf(".");if(-1==r)return n+=".00%";var t=n.substring(r+1,n.length);return 1==t.length?n+="0%":2==t.length?n+="%":n=n.substring(0,n.indexOf(".")+3)+"%"}});