eSoafApp.directive("eDialogbox",["$rootScope","$filter",function(a,e){return{restrict:"E",transclude:!0,replace:!0,template:function(e,o){return'<div id="DialogBox_Outside" style="pointer-events:all;-webkit-pointer-events:none;"><span ng-show="$counter.visit.length>0" id="DialogBox_Wrapper"style="animation: ngdialog-fadein 0.5s;padding:14px 6px 14px 6px;pointer-events:all;margin:0px auto;border:3px solid #6fb3e0;border-radius:.2em;background:transparent"><span style="animation: ngdialog-fadein 0.5s;cursor:pointer;margin:3px;padding:6px;color:#FFF;border-radius:.2em;background:#6fb3e0;font-size:14px;pointer-events:all;"ng-repeat="row in $counter.visit" ng-click="openDialog(row)" title="{{row.name}}"><i class="glyphicon glyphicon-file"></i>&nbsp;{{row.name | smallTitle}}</span></span></div>'},link:function(e,o,n,t,i){$("#DialogBox_Wrapper").on("DOMNodeInserted",function(e){"DOMNodeInserted"==e.type&&($(e.target).animate({color:"hsla(350,99%,55%,0.9)"},300).animate({color:"#FFF"},300).animate({color:"hsla(350,99%,55%,0.9)"},300).animate({color:"#FFF"},300),$(e.target).tooltip({tooltipClass:"e-tooltip-dialogbox",position:{at:"center-25 bottom+15"}}))}),e.openDialog=function(e){$(e.element).show(500,function(e){for(var o=a.$counter.visit,n=a.$counter.visit.length,t=0;t<n;t+=1)if(o[t].element==e){a.$counter.visit.splice(t,1);break}}(e.element))}}}}]);