eSoafApp.directive("eNumberRange",function(){return{restrict:"A",require:"ngModel",scope:{},link:function(e,n,r,i){if("number"===r.type&&(r.min||r.max)){var u,t;r.$observe("min",function(e){u=Number(e)}),r.$observe("max",function(e){t=Number(e)}),$(n).change(function(){Number(n.val())>t&&(i.$setViewValue(t),i.$render()),Number(n.val())<u&&(i.$setViewValue(u),i.$render())})}}}});