///
/// 日期格式化 filters
///
eSoafApp.filter("eDateTime",function(){return function(e){var n="/";if(null==e)return"";if(e.length>=6&&e.length<=8){var r=new RegExp("([0-9]{2,4})([0-9]{2})([0-9]{2})","g");if(r.test(e)){var t=e.replace(r,"$1"+n+"$2"+n+"$3");e=t}}return e}}),eSoafApp.filter("eMoney",function(){return function(e){return S2M(e)}});